import React, { Component, PropTypes } from 'react';
import { Note } from '../Model';
import {NotificationContainer, NotificationManager} from 'react-notifications';
import ReactTooltip from 'react-tooltip'
const WAIT_INTERVAL = 1000

class NoteBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
        	note: this.props.note,
        	expanded: false,
        }
    }

    titleChange(event) {
	    clearTimeout(this.titleTimer)
	    let prevIndex = this.state.note.id;
	    this.state.note.title = event.target.value;
	    this.setState({
	      note: this.state.note
	    })
	    this.titleTimer = setTimeout(this.triggerTitleChange.bind(this), WAIT_INTERVAL)
	}

	triggerTitleChange() {
		Note.updateTitle({
		  data:{
		    id:this.state.note.id,
		    title:this.state.note.title
		  },
		  onSuccess: function(data) {
		    NotificationManager.success(data.message);
		  }.bind(this),
		  onError: function(data) {

		  }
		})
	}

	deleteNote() {
      Note.deleteNote({
        data:{
          id:this.state.note.id
        },
        onSuccess: function(data) {
          this.setState({
            loading: false
          })
          NotificationManager.warning(data.message);
        }.bind(this),
        onError: function(data) {

        }
      })
	}

	noteChange(event) {
	    clearTimeout(this.noteTimer)
	    this.state.note.notes = event.target.value;
	    this.setState({
	      note: this.state.note
	    })
	    this.noteTimer = setTimeout(this.triggerNoteChange.bind(this), WAIT_INTERVAL)
	}

	triggerNoteChange(){
	    Note.updateNote({
	      data:{
	        id:this.state.note.id,
	        notes:this.state.note.notes
	      },
	      onSuccess: function(data) {
	         NotificationManager.success(data.message);
	      }.bind(this),
	      onError: function(data) {

	      }
	    })
	}

	toggleExpandNote() {
	    this.setState({
	    	expanded: !this.state.expanded
	    })
	} 

  	renderVersionHistory(versionData, expandIndex) {
	    return (
	    	<div className="version">
	          <h4 className="version-header">Version History</h4>
	          <ul>{versionData.map(this.renderSingleVersionBlock.bind(this))}</ul>
	        </div>
    	)
	}



    render() {
		let { note } = this.state;

        return(
	        <div className="form-group">
	          <div className= "input-container">
	            <input type="text" className="input-field" name="title" placeholder="Note Title" value={note.title} onChange={this.titleChange.bind(this)}/>
	            <button type="button" className="btn btn-sm input-button" ref = "expand" data-tip='Delete Note' data-for='delete' onClick={this.deleteNote.bind(this)}>❌</button>
	            <button type="button" className="btn btn-sm input-button" data-tip='Expand Note' data-for='expand' onClick={this.toggleExpandNote.bind(this)}>↗️</button>
	          </div>
	          <textarea onChange={this.noteChange.bind(this)} className="form-control text-area" rows="10" value={note.notes}/>
	          {this.renderVersionHistory()}
	          <ReactTooltip id='delete'>{}</ReactTooltip>
	          <ReactTooltip id='expand'>{}</ReactTooltip>
	        </div>
	      )
    }
}

export default NoteBlock;
