import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

const dummyData = [
  { id:1, title: "Naresh", notes: "i am not good boy", expand: false },
  { id:2, title: "Naveen", notes: "i am not good boy", expand: false },
  { id:3, title: "Dikson", notes: "i am not good boy", expand: false },
]


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      noteDataFromServer: [],
    };
  }

  componentWillMount() {
    //Data fetch from server

    // {
    //   this.setState({
    //     loading: false,
    //     noteDataFromServer: dataFromServer
    //   })
    // }

    this.setState({
      noteDataFromServer: dummyData,
      loading: false
    })

  }

  componentDidMount() {

  }

  setTextToState(text, index) {
    dummyData[index] = text;
  }

  handleChange(index, event) {
    this.state.noteDataFromServer[index].notes = event.target.value;
    this.setState({
      noteDataFromServer: this.state.noteDataFromServer
    })
  }

  addNewNote(){
    var sample =  [{ id:this.state.noteDataFromServer.length+1, title: "", notes: "", expand: false }];
    this.setState({
      noteDataFromServer: sample.concat(this.state.noteDataFromServer)
    })
  }

  renderNotes(data, index) {
    if(data.expand) {
      return(
          <div className="form-group1">
            <div className= "input-container">
              <input type="text" className="input-field1" name="title" placeholder="Note Title" value={this.state.noteDataFromServer[index].notes} onChange={this.handleChange.bind(this, index)}/>
              <button type="button" className="btn btn-sm input-button1">❌</button>
            </div>
            <textarea className="form-control text-area" rows="15"></textarea>
          </div>
      )  
    }
    else{
      return(
          <div className="form-group">
            <div className= "input-container">
              <input type="text" className="input-field" name="title" placeholder="Note Title" value={this.state.noteDataFromServer[index].notes} onChange={this.handleChange.bind(this, index)}/>
              <button type="button" className="btn btn-sm input-button">❌</button>
            </div>
            <textarea className="form-control text-area" rows="10"></textarea>
          </div>
      )
    }
  }

  render() {
    if(this.state.loading) {
      return (
        <div className="App">
          <p>loading....</p>
        </div>
      )
    } else {
      return (
        <div className="App">
          <header className="App-header">
            <div className="d-flex p-3 bg-secondary text-white m-2">  
              <button type="button" className="btn btn-primary btn-sm" onClick={this.addNewNote.bind(this)}>Add New Note</button>
            </div>
            <div className="row">
            {this.state.noteDataFromServer.map(this.renderNotes.bind(this))}
           </div> 
          </header>
        </div>
      );  
    }
  }
}

export default App;
