import React, { Component } from 'react';
import './App.css';
import { Note } from './Model';
import NoteBlock from './Components/NoteBlock';
import 'bootstrap/dist/css/bootstrap.css';
import ReactTooltip from 'react-tooltip'
import {findDOMNode} from 'react-dom'
import 'react-notifications/lib/notifications.css';
import 'react-notifications/dist/react-notifications.css';
import 'react-notifications/dist/react-notifications.js';
import {NotificationContainer, NotificationManager} from 'react-notifications';

const WAIT_INTERVAL = 1000

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      noteDataFromServer: [],
      expandIndex: -1
    };
  }

  titleTimer = null
  noteTimer = null

  //Data fetch from server
  componentWillMount() {
    Note.fetchNotes({
      onSuccess: function(data) {
        this.setState({
          noteDataFromServer: data.data,
          loading: false
        })
      }.bind(this),
      onError: function(data) {

      }
    })
  }

  componentDidUpdate(){
    ReactTooltip.hide(findDOMNode(this.refs.expand))
  }




  addNewNote(previousExpandIndex){
    var sample =  [{title: "", notes: "", expand: false }];
    if(previousExpandIndex != -1){
      this.minimizeNote(previousExpandIndex);
    }
    this.setState({
      noteDataFromServer :this.state.noteDataFromServer,
      noteDataFromServer: sample.concat(this.state.noteDataFromServer)
    })
    Note.addNewNote({
      onSuccess: function(data) {
        this.state.noteDataFromServer[0].id = data.id;
        this.setState({
          noteDataFromServer: this.state.noteDataFromServer
        })
      }.bind(this),
      onError: function(data) {

      }
    })
  }

  expandNote(index, previousExpandIndex) {
    this.state.noteDataFromServer[index].expand = true;
    this.state.noteDataFromServer[index].showVersions = false;
    if(previousExpandIndex != -1) {
      this.state.noteDataFromServer[previousExpandIndex].expand = false;  
    }

    this.setState({
      expandIndex: index,
      noteDataFromServer: this.state.noteDataFromServer
    })
  }

  minimizeNote(index) {
    this.state.noteDataFromServer[index].expand = false;
    this.state.noteDataFromServer[index].showVersions = false;

    this.setState({
      expandIndex: -1,
      noteDataFromServer: this.state.noteDataFromServer
    })
  }



  showVersions(index){
    this.state.noteDataFromServer[index].showVersions = true;
    Note.getNoteVersions({
      data:{
        id:this.state.noteDataFromServer[index].id,
      },
      onSuccess: function(data) {
        let versionData = data.data
        this.state.noteDataFromServer[index].versionData = versionData
        this.setState({
          noteDataFromServer: this.state.noteDataFromServer,
        })
      }.bind(this),
      onError: function(data) {

      }
    })
  }

  // renderNotes(data, index) {
  //   if(!data.expand) {
  //     var notesData = this.state.noteDataFromServer[index];
  //     var notes = this.state.noteDataFromServer[index].notes;
  //     return(
  //       <div key={index} className="form-group">
  //         <div className= "input-container">
  //           <input type="text" className="input-field" name="title" placeholder="Note Title" value={notesData.title} onChange={this.titleChange.bind(this, index)}/>
  //           <button type="button" className="btn btn-sm input-button" ref = "expand" data-tip='Delete Note' data-for='delete' onClick={this.deleteNote.bind(this, index)}>❌</button>
  //           <button type="button" className="btn btn-sm input-button" data-tip='Expand Note' data-for='expand' onClick={this.expandNote.bind(this, index, this.state.expandIndex)}>↗️</button>
  //         </div>
  //         <textarea onChange={this.notesChange.bind(this, index)} className="form-control text-area" rows="10" value={notes}/>
  //         <ReactTooltip id='delete'>{}</ReactTooltip>
  //         <ReactTooltip id='expand'>{}</ReactTooltip>
  //       </div>
  //     )
  //   }
  // }

  getSingleNoteData(data){
    this.state.noteDataFromServer[this.state.expandIndex].notes = data.notes;
    this.setState({
        noteDataFromServer: this.state.noteDataFromServer,
      })
  }

  renderSingleVersionBlock(data, expandIndex) {
    return(
      <li><button type="button" className="btn btn-sm" onClick={this.getSingleNoteData.bind(this,data)}>{data.modified_date}</button></li>
    )
  }

  renderVersionHistory(versionData, expandIndex) {
    if(!this.state.noteDataFromServer[expandIndex].showVersions){
      return (
        <div>
          <textarea onChange={this.notesChange.bind(this, expandIndex)} className="form-control text-area" rows="15" value={this.state.noteDataFromServer[expandIndex].notes}/>
        </div>
      )
    }
    else{
     return(
      <div className="notebox">
        <textarea onChange={this.notesChange.bind(this, expandIndex)} className="form-control text-area note" rows="15" value={this.state.noteDataFromServer[expandIndex].notes}/>
        <div className="version">
          <h4 className="version-header">Version History</h4>
          <ul>{versionData.map(this.renderSingleVersionBlock.bind(this))}</ul>
        </div>
      </div>
     )
    }
  }

  renderExpandNote(expandIndex) {
    if(expandIndex != -1) {
      console.log(this.state)
      var notesData = this.state.noteDataFromServer[expandIndex];
      var notes = this.state.noteDataFromServer[expandIndex].notes;
      var versionData = this.state.noteDataFromServer[expandIndex].versionData
      return(
        <div className="form-group1">
          <div className= "input-container">
            <input type="text" className="input-field1" name="title" placeholder="Note Title" value={notesData.title} onChange={this.titleChange.bind(this, this.state.expandIndex)}/>
            <button type="button" className="btn btn-sm input-button1" data-tip='Delete Note' data-for='delete' onClick={this.deleteNote.bind(this, this.state.expandIndex)}>❌</button>
            <button type="button" className="btn btn-sm input-button1" data-tip='Minimize Note Note' data-for='minimize' onClick={this.minimizeNote.bind(this, this.state.expandIndex)}>↙️</button>
            <button type="button" className="btn btn-sm input-button1" data-tip='Version History' data-for='history' onClick={this.showVersions.bind(this, this.state.expandIndex)}>🕒</button>
          </div>
          {this.renderVersionHistory(this.state.noteDataFromServer[expandIndex].versionData, expandIndex)}
          <ReactTooltip id='delete'>{}</ReactTooltip>
          <ReactTooltip id='minimize'>{}</ReactTooltip>
          <ReactTooltip id='history'>{}</ReactTooltip>
        </div>
      ) 
    }
  }


  renderNote = (note, index) => {
    return <NoteBlock note={note} key={index}/> 
  }

  render() {
    if(this.state.loading) {
      return (
        <div className="App">
          <p>loading....</p>
        </div>
      )
    } else {
      return (
        <div className="App">
            <div className="d-flex p-3 bg-secondary text-white m-2">  
              <button type="button" className="btn btn-primary btn-sm" onClick={this.addNewNote.bind(this,this.state.expandIndex)}>Add New Note</button>
            </div>
            {this.renderExpandNote(this.state.expandIndex)}
            <div className="row">
              {this.state.noteDataFromServer.map(this.renderNote.bind(this))}
            </div> 
            <NotificationContainer/>
        </div>
      );  
    }
  }
}

export default App;
