import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Note } from './Model';

const dummyData = [
  { id:1, title: "Naresh kumar", notes: "i am not good boy", expand: false },
  { id:2, title: "Naveen pg", notes: "i am worst", expand: false },
  { id:3, title: "Diksonsamuel ", notes: "i am rich", expand: false },
  { id:4, title: "Naresh", notes: "i am poor", expand: false },
  { id:5, title: "Naveen", notes: "i am  good boy", expand: false },
  { id:6, title: "Dikson", notes: "i am god", expand: false },
]

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      noteDataFromServer: [],
      expandIndex: -1
    };
  }

  componentWillMount() {
    Note.fetchNotes({
      onSuccess: function(data) {
        this.setState({
          loading: false,
          noteDataFromServer: data
        })
      },
      onError: function(data) {

      }
    })
    //Data fetch from server

    // {
    //   this.setState({
    //     loading: false,
    //     noteDataFromServer: dataFromServer
    //   })
    // }

    // this.setState({
    //   noteDataFromServer: dummyData,
    //   loading: false
    // })

  }

  componentDidMount() {

  }

  setTextToState(text, index) {
    dummyData[index] = text;
  }

  handleChange(index, event) {
    this.state.noteDataFromServer[index].notes = event.target.value;
    this.setState({
      noteDataFromServer: this.state.noteDataFromServer
    })
  }

  titleChange(index, event) {
    this.state.noteDataFromServer[index].title = event.target.value;
    this.setState({
      noteDataFromServer: this.state.noteDataFromServer
    })
  }

  addNewNote(){
    var sample =  [{ id:this.state.noteDataFromServer.length+1, title: "", notes: "", expand: false }];
    this.setState({
      noteDataFromServer: sample.concat(this.state.noteDataFromServer)
    })
  }

  expandNote(index, previousExpandIndex) {
    this.state.noteDataFromServer[index].expand = true;
    if(previousExpandIndex != -1) {
      this.state.noteDataFromServer[previousExpandIndex].expand = false;  
    }

    this.setState({
      expandIndex: index,
      noteDataFromServer: this.state.noteDataFromServer
    })

  }

  deleteNote() {

  }

  saveNote(index){

    console.log(this.state.noteDataFromServer[index])
  }

  renderNotes(data, index) {
    if(!data.expand) {
      return(
        <div className="form-group">
          <div className= "input-container">
            <input type="text" className="input-field" name="title" placeholder="Note Title" value={this.state.noteDataFromServer[index].title} onChange={this.titleChange.bind(this, index)}/>
            <button type="button" className="btn btn-sm input-button" onClick={this.deleteNote.bind(this)}>❌</button>
            <button type="button" className="btn btn-sm input-button" onClick={this.saveNote.bind(this, index)}>Save</button>
            <button type="button" className="btn btn-sm input-button" onClick={this.expandNote.bind(this, index, this.state.expandIndex)}>↗</button>
          </div>
          <textarea onChange={this.handleChange.bind(this, index)} className="form-control text-area" rows="10" value={this.state.noteDataFromServer[index].notes}/>
        </div>
      )
    }
  }

  renderExpandNote(expandIndex) {
    if(expandIndex != -1) {
      var notesData = this.state.noteDataFromServer[expandIndex];
      var notes = this.state.noteDataFromServer[expandIndex].notes;
      return(
        <div className="form-group1">
          <div className= "input-container">
            <input type="text" className="input-field1" name="title" placeholder="Note Title" value={notesData.title} onChange={this.handleChange.bind(this, this.state.expandId -1)}/>
            <button type="button" className="btn btn-sm input-button1">❌</button>
          </div>
          <textarea onChange={this.handleChange.bind(this, expandIndex)} className="form-control text-area" rows="15" value={notes}/>
        </div>
      ) 
    }
  }

  render() {
    if(this.state.loading) {
      return (
        <div className="App">
          <p>loading....</p>
        </div>
      )
    } else {
      return (
        <div className="App">
          <header className="App-header">
            <div className="d-flex p-3 bg-secondary text-white m-2">  
              <button type="button" className="btn btn-primary btn-sm" onClick={this.addNewNote.bind(this)}>Add New Note</button>
            </div>
            {this.renderExpandNote(this.state.expandIndex)}
            <div className="row">
              {this.state.noteDataFromServer.map(this.renderNotes.bind(this))}
            </div> 
          </header>
        </div>
      );  
    }
  }
}

export default App;
