import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Note } from './Model';
import ReactTooltip from 'react-tooltip'
import {findDOMNode} from 'react-dom'
import Notification from './Notification'




class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      noteDataFromServer: [],
      expandIndex: -1
    };
  }

  //Data fetch from server
  componentWillMount() {
    Note.fetchNotes({
      onSuccess: function(data) {
        this.setState({
          noteDataFromServer: data.data,
          loading: false
        })
      }.bind(this),
      onError: function(data) {

      }
    })
  }

  componentDidUpdate(){
    ReactTooltip.hide(findDOMNode(this.refs.expand))
  }

  notesChange(index, event) {
    this.state.noteDataFromServer[index].notes = event.target.value;
    this.setState({
      noteDataFromServer: this.state.noteDataFromServer
    })
    Note.updateNote({
      data:{
        id:this.state.noteDataFromServer[index].id,
        notes:this.state.noteDataFromServer[index].notes
      },
      onSuccess: function(data) {
        // this.setState({
        //   noteDataFromServer: data.data,
        //   loading: false
        // })
      }.bind(this),
      onError: function(data) {

      }
    })
  }

  titleChange(index, event) {
    let prevIndex = this.state.noteDataFromServer[index].id;
    console.log(prevIndex)
    this.state.noteDataFromServer[index].title = event.target.value;
    this.setState({
      noteDataFromServer: this.state.noteDataFromServer
    })

    if(prevIndex){
      Note.updateTitle({
        data:{
          id:this.state.noteDataFromServer[index].id,
          title:this.state.noteDataFromServer[index].title
        },
        onSuccess: function(data) {
          alert("title updated")
          return(
            <div class="alert alert-success">
              <strong>Title Updated Succesfully!</strong>
            </div>

          )
        }.bind(this),
        onError: function(data) {
          alert("title updated")
        }
      })
    }
    else{
      Note.addNewNote({
        data:{
          title:this.state.noteDataFromServer[index].title
        },
        onSuccess: function(data) {
          alert(data)
          this.setState({
            // this.state.noteDataFromServer[index].id = 
            // noteDataFromServer: data.data,
          })
          alert("Title Saved")
        }.bind(this),
        onError: function(data) {

        }
      })
    }
  }

  addNewNote(){
    var sample =  [{title: "", notes: "", expand: false }];
    this.setState({
      noteDataFromServer: sample.concat(this.state.noteDataFromServer)
    })
  }

  expandNote(index, previousExpandIndex) {
    this.state.noteDataFromServer[index].expand = true;
    this.state.noteDataFromServer[index].showVersions = false;
    if(previousExpandIndex != -1) {
      this.state.noteDataFromServer[previousExpandIndex].expand = false;  
    }

    this.setState({
      expandIndex: index,
      noteDataFromServer: this.state.noteDataFromServer
    })
  }

  minimizeNote(index) {
    this.state.noteDataFromServer[index].expand = false;

    this.setState({
      expandIndex: -1,
      noteDataFromServer: this.state.noteDataFromServer
    })
  }

  deleteNote(index) {
    let previousIndex = this.state.noteDataFromServer[index].id;
    if(previousIndex){
      Note.deleteNote({
        data:{
          id:this.state.noteDataFromServer[index].id
        },
        onSuccess: function(data) {
          this.state.noteDataFromServer.splice(index,1);
          this.setState({
            noteDataFromServer: this.state.noteDataFromServer,
            loading: false
          })
        }.bind(this),
        onError: function(data) {

        }
      })
    }
    else{
      this.state.noteDataFromServer.splice(index,1);
      this.setState({
        noteDataFromServer: this.state.noteDataFromServer,
        loading: false
      })
    }
  }

  showVersions(index){
    this.state.noteDataFromServer[index].showVersions = true;
    Note.getNoteVersions({
      data:{
        id:this.state.noteDataFromServer[index].id,
      },
      onSuccess: function(data) {
        let versionData = data.data
        this.state.noteDataFromServer[index].versionData = versionData
        this.setState({
          noteDataFromServer: this.state.noteDataFromServer,
        })
      }.bind(this),
      onError: function(data) {

      }
    })
      console.log(this.state)
  }

  renderNotes(data, index) {
    if(!data.expand) {
      var notesData = this.state.noteDataFromServer[index];
      var notes = this.state.noteDataFromServer[index].notes;
      return(
        <div className="form-group">
          <div className= "input-container">
            <input type="text" className="input-field" name="title" placeholder="Note Title" value={notesData.title} onChange={this.titleChange.bind(this, index)}/>
            <button type="button" className="btn btn-sm input-button" ref = "expand" data-tip='Delete Note' data-for='delete' onClick={this.deleteNote.bind(this, index)}>‚ùå</button>
            <button type="button" className="btn btn-sm input-button" data-tip='Expand Note' data-for='expand' onClick={this.expandNote.bind(this, index, this.state.expandIndex)}>‚ÜóÔ∏è</button>
          </div>
          <textarea onChange={this.notesChange.bind(this, index)} className="form-control text-area" rows="10" value={notes}/>
          <ReactTooltip id='delete'>{}</ReactTooltip>
          <ReactTooltip id='expand'>{}</ReactTooltip>
        </div>
      )
    }
  }

  renderSingleVersionBlock(data, index) {
    return(
      <button>{data.modified_date}</button>
    )
  }

  renderVersionHistory(versionData) {
    if(versionData && versionData.length >0) {
      return(
        <div  className="notebox">
          {versionData.map(this.renderSingleVersionBlock.bind(this))}
        </div>
      )  
    }

  }        

  renderExpandNote(expandIndex) {
    if(expandIndex != -1) {
      var notesData = this.state.noteDataFromServer[expandIndex];
      var notes = this.state.noteDataFromServer[expandIndex].notes;
      var versionData = this.state.noteDataFromServer[expandIndex].versionData
      return(
        <div className="form-group1">
          <div className= "input-container">
            <input type="text" className="input-field1" name="title" placeholder="Note Title" value={notesData.title} onChange={this.titleChange.bind(this, this.state.expandIndex)}/>
            <button type="button" className="btn btn-sm input-button1" data-tip='Delete Note' data-for='delete' onClick={this.deleteNote.bind(this, this.state.expandIndex)}>‚ùå</button>
            <button type="button" className="btn btn-sm input-button1" data-tip='Minimize Note Note' data-for='minimize' onClick={this.minimizeNote.bind(this, this.state.expandIndex)}>‚ÜôÔ∏è</button>
            <button type="button" className="btn btn-sm input-button1" data-tip='Version History' data-for='history' onClick={this.showVersions.bind(this, this.state.expandIndex)}>üïí</button>
          </div>

          <textarea onChange={this.notesChange.bind(this, expandIndex)} className="form-control text-area" rows="15"value={notes}/>
            {this.renderVersionHistory(this.state.noteDataFromServer[expandIndex].versionData)}
          <ReactTooltip id='delete'>{}</ReactTooltip>
          <ReactTooltip id='minimize'>{}</ReactTooltip>
          <ReactTooltip id='history'>{}</ReactTooltip>
        </div>
      ) 
    }
  }

  render() {
    if(this.state.loading) {
      return (
        <div className="App">
          <p>loading....</p>
        </div>
      )
    } else {
      return (
        <div className="App">
          <header className="App-header">
            <div className="d-flex p-3 bg-secondary text-white m-2">  
              <button type="button" className="btn btn-primary btn-sm" onClick={this.addNewNote.bind(this)}>Add New Note</button>
            </div>
            {this.renderExpandNote(this.state.expandIndex)}
            <div className="row">
              {this.state.noteDataFromServer.map(this.renderNotes.bind(this))}
            </div> 
          </header>
        </div>
      );  
    }
  }
}

export default App;
