import axios from 'axios';


class Api {
  static headers() {
    return {
      'Accept': 'application/json',
      'Content-Type': 'application/json',
      'dataType': 'json',
    }
  }

  static get(route, params) {
    alert('ehlll')
    return this.xhr(route, params, 'GET');
  }

  static put(route, params) {
    return this.xhr(route, params, 'PUT')
  }

  static post(route, params) {
    return this.xhr(route, params, 'POST')
  }

  static delete(route, params) {
    return this.xhr(route, params, 'DELETE')
  }

  static patch(route, params) {
    return this.xhr(route, params, 'PATCH')
  }

  static xhr(route, params, verb) {
    var dataOption = {};
    if (params && verb === 'GET') {
      dataOption['params'] = params;
    } else {
      dataOption['data'] = params;
    }

    let options = Object.assign({ method: verb, url: route }, dataOption );
    options.headers = Api.headers();

    return axios(options)
              .catch(function (error) {
                if (error.response) {
                  alert('hello 33')
                  throw new Error(error.response.data.message);
                } else if (error.request) {
                  throw new Error("Unable to connect to the Internet.");
                } else {
                  throw new Error(error.message);
                }
            })
            .then(function(response) {
              // if(response.headers.authorization) {
              // }
              alert('hello 22');
              return response;
            })
            .then(handleErrors)
            .then((responseJson) => {
              alert('hello 11');

              return responseJson.data;
            })
  }

}

function handleErrors(response) {
  alert('hey')
  if (response.status) {
    switch(response.status) {
      case 401:
        throw new Error(response.message);
        break;
      case 403:
        throw new Error(response.message);
        break;
      case 400:
        throw new Error(response.message);
        break;    
    }
  }
  return response;
}

//Lib Functions
function userLoggedIn() {
  return false
}

function getUserAccessToken() {
  return "aaaa";
}



export default Api;
